if (global.get('enableFlow')) {
    var entity_id = env.get("entity_id");
    if (entity_id === undefined || "off" !== global.get("homeassistant.homeAssistant.states['" + entity_id + "'].state")) {
        return msg;        
    }
}

return {
    payload: {
        data: {
            target: global.get("secrets")['telegram_danilov_info_group_id'],
            message: msg.payload
        }
    }
};

var main_automation_entity_ids = Object.keys(global.get("homeassistant").homeAssistant.states)
    .filter(key => key.endsWith('_main'));

return {
    "payload": {
        "domain": "homeassistant",
        "service": msg.payload ? "turn_on" : "turn_off",
        "target": {
            "entity_id": main_automation_entity_ids
        }
    }
};
